package com.intfocus.yh_android;import android.os.Bundle;import android.app.Activity;import android.widget.TextView;import com.intfocus.yh_android.util.FileUtil;import com.intfocus.yh_android.util.K;public class DeveloperActivity extends Activity {    private TextView behaviorTv,settingTv,notificationTv;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_developer);        findViewById();        initData();    }    public void findViewById() {        behaviorTv = (TextView) findViewById(R.id.behaviorTv);        settingTv = (TextView) findViewById(R.id.settingTv);        notificationTv = (TextView) findViewById(R.id.notificationTv);    }    public void initData() {        String pushMessagePath = String.format("%s/%s", FileUtil.basePath(this), K.kPushMessageFileName);        if (FileUtil.readFile(pushMessagePath) != null){            behaviorTv.setText(FileUtil.readFile(pushMessagePath));        }        String noticePath = FileUtil.dirPath(this, K.kConfigDirName, K.kLocalNotificationConfigFileName);        if (FileUtil.readFile(noticePath) != null){            notificationTv.setText(FileUtil.readFile(noticePath));        }        String settingsConfigPath = FileUtil.dirPath(this, K.kConfigDirName, K.kSettingConfigFileName);        if (FileUtil.readFile(settingsConfigPath) != null){            settingTv.setText(FileUtil.readFile(settingsConfigPath));        }    }}