package com.intfocus.yonghuitest;import android.os.Bundle;import android.view.View;import android.widget.AdapterView;import android.widget.ArrayAdapter;import android.widget.ListView;import android.widget.TextView;import java.util.ArrayList;public class DeveloperActivity extends BaseActivity {    private ArrayAdapter<String> mListAdapter;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_developer);        initSettingListItem();    }    /*     * 个人信息页菜单项初始化     */    private void initSettingListItem() {        ArrayList<String> listItem = new ArrayList<>();        String[] itemName = {"报表缓存数据列表", "请求头缓存列表", "配置文件列表"};        for (int i = 0; i < itemName.length; i++) {            listItem.add(itemName[i]);        }        mListAdapter = new ArrayAdapter(this, R.layout.list_item_setting, R.id.item_setting, listItem);        ListView listView = (ListView) findViewById(R.id.list_setting);        listView.setAdapter(mListAdapter);        listView.setOnItemClickListener(mListItemListener);    }    /*     * 个人信息菜单项点击事件     */    private ListView.OnItemClickListener mListItemListener = new ListView.OnItemClickListener() {        @Override        public void onItemClick(AdapterView<?> arg0, View arg1, int arg2, long arg3) {            TextView mItemText = (TextView) arg1.findViewById(R.id.item_setting);            switch (mItemText.getText().toString()) {                case "报表缓存数据列表" :                    break;                case "请求头缓存列表" :                    break;                case "配置文件列表" :                    break;            }        }    };    public void dismissActivity(View v) {        DeveloperActivity.this.onBackPressed();    }}